import React, { useState } from 'react';
import './EducatorSignUp.css';
import { debounce } from 'lodash';
import api from './api';
function EducatorSignUp() {
    const [formData, setFormData] = useState({
        name: '',
        dob: '',
        highest_qualification: '',
        email: '',
        mobile_no: '',
        username: '',
        password: '',
    });

    const [otp, setOtp] = useState('');
    const [otpSent, setOtpSent] = useState(false);
    const [isOtpVerified, setIsOtpVerified] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [errorFields, setErrorFields] = useState({
        email: '',
        mobile_no: '',
        username: '',
    });

    const [passwordErrors, setPasswordErrors] = useState([]);

    // Handle input change events
    const debouncedCheckUsernameAvailability = debounce(async (username) => {
        try {
            // Make an API request to check if the username is available
            const response = await api.post('/check-educator-unique-fields/', {
                username,
            },{headers: {
                'X-CSRFToken': csrfToken, // Add CSRF token to headers
            },});

            // Update error fields based on the response data
            const isUsernameAvailable = response.data.username;
            setErrorFields((prevErrorFields) => ({
                ...prevErrorFields,
                username: isUsernameAvailable ? '' : 'Username is already in use.',
            }));
        } catch (error) {
            console.error('Error checking username availability:', error);
            setErrorFields((prevErrorFields) => ({
                ...prevErrorFields,
                username: 'Error checking username availability. Please try again.',
            }));
        }
    }, 300);

    // Define debounced function to check uniqueness of email and mobile_no fields
    const debouncedCheckEducatorUniqueFields = debounce(async (formData) => {
        try {
            // Make an API request to check the uniqueness of email and mobile_no fields
            const response = await api.post('/check-educator-unique-fields/', {
                email: formData.email,
                mobile_no: formData.mobile_no,
            },{headers: {
                'X-CSRFToken': csrfToken, // Add CSRF token to headers
            },});

            // Update error fields based on the response data
            setErrorFields({
                email: response.data.email ? '' : 'Email is already in use.',
                mobile_no: response.data.mobile_no ? '' : 'Mobile number is already in use.',
            });
        } catch (error) {
            console.error('Error checking unique fields:', error);
            setErrorFields({
                email: 'Error checking email. Please try again.',
                mobile_no: 'Error checking mobile number. Please try again.',
            });
        }
    }, 300);

    function validatePassword(password) {
        const minLength = 8;
        const hasUppercase = /[A-Z]/.test(password);
        const hasLowercase = /[a-z]/.test(password);
        const hasSpecialCharacter = /[!@#$%^&*()-_=+[\]{};':"\\|,.<>/?~`]/.test(password);
    
        let errorMessages = [];
    
        if (password.length < minLength) {
            errorMessages.push('Password must be at least 8 characters long.');
        }
        if (!hasUppercase) {
            errorMessages.push('Password must contain at least one uppercase letter.');
        }
        if (!hasLowercase) {
            errorMessages.push('Password must contain at least one lowercase letter.');
        }
        if (!hasSpecialCharacter) {
            errorMessages.push('Password must contain at least one special character.');
        }
    
        return errorMessages;
    }


    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevFormData) => ({
            ...prevFormData,
            [name]: value,
        }));
    
        // Handle real-time username availability check
        if (name === 'username') {
            debouncedCheckUsernameAvailability(value);
        }
    
        // Trigger password validation when password changes
        if (name === 'password') {
            const errors = validatePassword(value);
            setPasswordErrors(errors);
        }
    };
    

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
    
        // Check for password errors
        if (passwordErrors.length > 0) {
            alert('Please fix password errors before submitting.');
            return;
        }
    
        await debouncedCheckEducatorUniqueFields(formData, setErrorFields);
        // If there are any errors, alert the user and stop form submission
        if (errorFields.email || errorFields.mobile_no) {
            alert('Please resolve the highlighted errors before submitting the form.');
            return;
        }
    
        // If all unique field checks pass, proceed to send OTP
        setIsLoading(true);
        try {
            const response = await api.post('/learner-send-otp/', { email: formData.email },{headers: {
                'X-CSRFToken': csrfToken, // Add CSRF token to headers
            },});
            alert(response.data.message);
            setOtpSent(true);
        } catch (error) {
            console.error('Error sending OTP:', error);
            alert('Failed to send OTP. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleOTPVerification = async (e) => {
        e.preventDefault();
        setIsLoading(true);
    
        try {
            const response = await api.post('/educator-verify-otp/', {
                email: formData.email,
                otp: otp,
            },{headers: {
                'X-CSRFToken': csrfToken, // Add CSRF token to headers
            },});
    
            if (response.data.success) {
                setIsOtpVerified(true);
                alert('OTP verified successfully!');
                // Proceed to learner signup
                await handleEducatorSignup();
            } else {
                alert('Incorrect OTP. Please try again.');
            }
        } catch (error) {
            console.error('Error verifying OTP:', error);
            alert('Error verifying OTP. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleResendOTP = async () => {
        setIsLoading(true);
        try {
            const response = await api.post('/educator-send-otp/', {
                email: formData.email,
            },{headers: {
                'X-CSRFToken': csrfToken, // Add CSRF token to headers
            },});
            alert(response.data.message);
            setOtpSent(true);
            setOtp('');
        } catch (error) {
            console.error('Error sending OTP:', error);
            alert('Failed to send OTP. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleEducatorSignup = async () => {
        try {
            const dataToSend = {
                ...formData,
                otp: otp,
            };

            const response = await api.post('/educator-signup/', dataToSend,{headers: {
                'X-CSRFToken': csrfToken, // Add CSRF token to headers
            },});

            if (response.status === 201) {
                alert('Educator signed up successfully!');
            } else {
                alert(`Error: ${response.data.message}`);
            }
        } catch (error) {
            console.error('Error during signup:', error);
            alert('Error during signup. Please try again.');
        }
    };

    return (
        <div className="container4">
            <h2>Educator Sign Up</h2>
            {isLoading && <p>Loading...</p>}
            {!isOtpVerified ? (
                <>
                    {otpSent ? (
                        <form onSubmit={handleOTPVerification}>
                            <label>Enter OTP</label>
                            <input
                                type="text"
                                value={otp}
                                onChange={(e) => setOtp(e.target.value)}
                                required
                            />
                            <input type="submit" value="Verify OTP" />
                            <button type="button" onClick={handleResendOTP}>
                                Resend OTP
                            </button>
                        </form>
                    ) : (
                        <form onSubmit={handleSubmit}>
                            <label>Full Name</label>
                            <input
                                type="text"
                                name="name"
                                placeholder="e.g. Thomas Alva Edison"
                                value={formData.name}
                                onChange={handleInputChange}
                                required
                            />
                            <br />

                            <label>Date of Birth</label>
                            <input
                                type="date"
                                name="dob"
                                value={formData.dob}
                                onChange={handleInputChange}
                                required
                            />
                            <br />

                            <label>Highest Qualification</label>
                            <select
                                name="highest_qualification"
                                value={formData.highest_qualification}
                                onChange={handleInputChange}
                                required
                            >
                                <option value="" disabled>
                                    Select your qualification
                                </option>
                                <option value="B.A.">Bachelor of Arts (B.A.)</option>
                                <option value="B.Sc.">Bachelor of Science (B.Sc.)</option>
                                <option value="B.Com">Bachelor of Commerce (B.Com)</option>
                                <option value="B.E.">Bachelor of Engineering (B.E.)</option>
                                <option value="B.Tech">Bachelor of Technology (B.Tech)</option>
                                <option value="B.Pharm">Bachelor of Pharmacy (B.Pharm)</option>
                                <option value="B.Arch">Bachelor of Architecture (B.Arch)</option>
                                <option value="BBA">Bachelor of Business Administration (BBA)</option>
                                <option value="BCA">Bachelor of Computer Applications (BCA)</option>
                                <option value="MBBS">Bachelor of Medicine, Bachelor of Surgery (MBBS)</option>
                                <option value="BDS">Bachelor of Dental Surgery (BDS)</option>
                                <option value="B.V.Sc.">Bachelor of Veterinary Science (B.V.Sc.)</option>
                                <option value="LL.B">Bachelor of Laws (LL.B)</option>
                                <option value="M.A.">Master of Arts (M.A.)</option>
                                <option value="M.Sc.">Master of Science (M.Sc.)</option>
                                <option value="M.Com">Master of Commerce (M.Com)</option>
                                <option value="M.E.">Master of Engineering (M.E.)</option>
                                <option value="M.Tech">Master of Technology (M.Tech)</option>
                                <option value="MBA">Master of Business Administration (MBA)</option>
                                <option value="MCA">Master of Computer Applications (MCA)</option>
                                <option value="MD">Doctor of Medicine (MD)</option>
                                <option value="MS">Master of Surgery (MS)</option>
                                <option value="M.Pharm">Master of Pharmacy (M.Pharm)</option>
                                <option value="LL.M">Master of Laws (LL.M)</option>
                                <option value="Ph.D">Doctor of Philosophy (Ph.D)</option>
                            </select>
                            <br></br>
                            <label>Email</label>
                            <input
                                type="text"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                onSubmit={handleSubmit}
                                required
                            />
                            {errorFields.email && <p style={{ color: 'red' }}>{errorFields.email}</p>}
                            <br />

                            <label>Mobile No</label>
                            <input
                                type="text"
                                name="mobile_no"
                                value={formData.mobile_no}
                                onChange={handleInputChange}
                                onSubmit={handleSubmit}
                                required
                            />
                            {errorFields.mobile_no && <p style={{ color: 'red' }}>{errorFields.mobile_no}</p>}
                            <br />

                            <label>Username</label>
                            <input
                                type="text"
                                name="username"
                                value={formData.username}
                                onChange={handleInputChange}
                                required
                            />
                            {errorFields.username && <p style={{ color: 'red' }}>{errorFields.username}</p>}
                            <br />

                            <label>Password</label>
                            <input
                                type="password"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                required
                            />
                            {passwordErrors.map((error, index) => (
                                <p key={index} style={{ color: 'red' }}>
                                    {error}
                                </p>
                            ))}
                            <br />

                            <input type="submit" value="Sign Up" />
                        </form>
                    )}
                    <a href="/educator-signin">Already have an Account? Sign In</a>
                </>
            ) : (
                // Code here to render once OTP is verified
                <div>
                    <p>OTP verified successfully! Proceed with the next steps.</p>
                </div>
            )}
        </div>
    );
}

export default EducatorSignUp;
