import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from './api';
import './LearnerSignIn.css';

function LearnerSignIn() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate(); // Correct usage of useNavigate hook

    const handleSubmit = async (e) => {
        e.preventDefault();
    
        try {
            // Send the email and password to the backend
            const response = await api.post('/learner-login/', {
                email,
                password,
            });
    
            // Check the response success status
            if (response.data.success) {
                // Redirect to learner space page
                navigate('/learner-space');
            } else {
                // Display error message to the user
                alert(response.data.message);
            }
        } catch (error) {
            console.error('Error during login:', error);
            alert('Failed to login. Please try again.');
        }
    };
    

    return (
        <div className="learner-signin">
            <header>
                <h1>Welcome to Learner's Space.</h1>
            </header>
            <div className="container3">
                <h2>Learner Login</h2>
                <form onSubmit={handleSubmit}>
                    <label>Email</label>
                    <input
                        type="text"
                        placeholder="email"
                        name="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <br />
                    <label>Password</label>
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <br />
                    <input type="submit" value="Login" />
                </form>
                <a href="/learner-signup">Don't have an Account? Sign Up</a>
            </div>
        </div>
    );
}

export default LearnerSignIn;
