// AdminLearners.js

import React, { useState, useEffect } from 'react';

function AdminLearners() {
    const [learnerCourses, setLearnerCourses] = useState([]);

    useEffect(() => {
        fetchLearnerCourses();
    }, []);

    const fetchLearnerCourses = () => {
        fetch('/api/learner-courses/')
            .then(response => response.json())
            .then(data => {
                setLearnerCourses(data.filter(course => !course.course_purchase_status));
            })
            .catch(error => console.error('Error fetching learner courses:', error));
    };

    const grantCourse = (learnerId, courseId) => {
        fetch(`/api/enroll-course/${learnerId}/${courseId}/`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
                'X-CSRFToken': getCookie('csrftoken')  // Fetch CSRF token from cookies
            }
        })
        .then(response => {
            if (response.ok) {
                alert('Course granted successfully');
                fetchLearnerCourses(); // Refresh learner courses after granting
            } else {
                alert('Failed to grant course');
            }
        })
        .catch(error => {
            console.error('Error granting course:', error);
            alert('Failed to grant course');
        });
    };

    const getCookie = (name) => {
        let cookieValue = null;
        if (document.cookie && document.cookie !== '') {
            const cookies = document.cookie.split(';');
            for (let i = 0; i < cookies.length; i++) {
                const cookie = cookies[i].trim();
                if (cookie.startsWith(name + '=')) {
                    cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                    break;
                }
            }
        }
        return cookieValue;
    };

    return (
        <div>
            <h1>Admin Learners</h1>
            <table>
                <thead>
                    <tr>
                        <th>Learner Name</th>
                        <th>Learner ID</th>
                        <th>Course ID</th>
                        <th>Course Name</th>
                        <th>Educator ID</th>
                        <th>Educator Name</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {learnerCourses.map(course => (
                        <tr key={course.course_id}>
                            <td>{course.learner_name}</td>
                            <td>{course.learner_id}</td>
                            <td>{course.course_id}</td>
                            <td>{course.course_name}</td>
                            <td>{course.educator_id}</td>
                            <td>{course.educator_name}</td>
                            <td>
                                <button onClick={() => grantCourse(course.learner_id, course.course_id)}>
                                    Grant Course
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default AdminLearners;
