//react-project/src/LearnerSignUp.js
import React, { useState } from 'react';
import './LearnerSignUp.css';
import api from './api';
import { checkUniqueFields } from './api';

// Function to validate the password according to the custom validator rules
function validatePassword(password) {
    const minLength = 8;
    const hasUppercase = /[A-Z]/.test(password);
    const hasSpecialCharacter = /[!@#$%^&*()-_=+[\]{};':"\\|,.<>/?~`]/.test(password);
  
    let errorMessages = [];
  
    if (password.length < minLength) {
        errorMessages.push('Password must be at least 8 characters long.');
    }
    if (!hasUppercase) {
        errorMessages.push('Password must contain at least one uppercase letter.');
    }
    if (!hasSpecialCharacter) {
        errorMessages.push('Password must contain at least one special character.');
    }
  
    return errorMessages;
}

function LearnerSignUp() {
    const [formData, setFormData] = useState({
        name: '',
        dob: '',
        highest_qualification: '',
        email: '',
        mobile_no: '',
        username: '',
        password: '',
    });

    const [otp, setOtp] = useState('');
    const [otpSent, setOtpSent] = useState(false);
    const [isOtpVerified, setIsOtpVerified] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [passwordErrors, setPasswordErrors] = useState([]);
  
    // Handle form input changes
    async function handleInputChange(e) {
        const { name, value } = e.target;
        setFormData((prevFormData) => ({
            ...prevFormData,
            [name]: value,
        }));
    
        if (name === 'email' || name === 'mobile_no' || name === 'username') {
            const isUnique = await checkUniqueFields(formData.email, formData.mobile_no, formData.username);
            if (!isUnique.email) {
                alert('Email is already in use.');
            }
            if (!isUnique.mobile_no) {
                alert('Mobile number is already in use.');
            }
            if (!isUnique.username) {
                alert('Username is already in use.');
            }
        } else if (name === 'password') {
            const errors = validatePassword(value);
            setPasswordErrors(errors);
        }
    }
    
    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (passwordErrors.length > 0) {
            alert('Please fix password errors before submitting.');
            return;
        }

        setIsLoading(true);

        try {
            const response = await api.post('/send-otp/', { email: formData.email });
            alert(response.data.message);
            setOtpSent(true);
        } catch (error) {
            console.error('Error sending OTP:', error);
            alert('Failed to send OTP. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    // Handle OTP verification and learner signup omitted for brevity...
    // (No changes needed for those sections)


    return (
        <div className="container4">
            <h2>Learner Sign Up</h2>
            {isLoading && <p>Loading...</p>}
            {!isOtpVerified ? (
                <>
                    {otpSent ? (
                        <>
                            <form onSubmit={handleOTPVerification}>
                                <label>Enter OTP</label>
                                <input
                                    type="text"
                                    value={otp}
                                    onChange={(e) => setOtp(e.target.value)}
                                    required
                                />
                                <input type="submit" value="Verify OTP" />
                                <button type="button" onClick={handleResendOTP}>
                                    Resend OTP
                                </button>
                            </form>
                        </>
                    ) : (
                        // Show the signup form
                        <form onSubmit={handleSubmit}>
                            <label>Full Name</label>
                            <input type="text" name="name" placeholder="e.g. Thomas Alva Edison" value={formData.name} onChange={handleInputChange} required/><br></br>
                            <label>Date of Birth</label><input type="date" name="dob" value={formData.dob} onChange={handleInputChange} required/><br></br>
                            <label>Highest Qualification</label><select name="highest_qualification" value={formData.highest_qualification} onChange={handleInputChange} required >
