import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from './api';
import './LearnerSignIn.css';

function getCSRFToken() {
    const cookieName = 'csrftoken';
    const cookies = document.cookie.split('; ');
    for (const cookie of cookies) {
        const [name, value] = cookie.split('=');
        if (name === cookieName) {
            return value;
        }
    }
    return null; // Return null if CSRF token not found
}

// Retrieve CSRF token using the getCSRFToken function
const csrfToken = getCSRFToken();

function LearnerSignIn() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate(); // Correct usage of useNavigate hook

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await api.post('/learner-login/', { email, password }, { headers: {
                    'X-CSRFToken': csrfToken, },// Add CSRF token to headers
                });
            if (response.data.success) {
                const learnerId = response.data.learner_id;
                navigate(`/learner-space/${learnerId}`);
            } else {
                alert(response.data.message);
            }
        } catch (error) {
            console.error('Error during login:', error);
            alert('Failed to login. Please try again.');
        }
    };
    

    return (
        <div className="learner-signin">
            <header>
                <h1>Welcome to Learner's Space.</h1>
            </header>
            <div className="container3">
                <h2>Learner Login</h2>
                <form onSubmit={handleSubmit}>
                    <label>Email</label>
                    <input
                        type="text"
                        placeholder="email"
                        name="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <br />
                    <label>Password</label>
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <br />
                    <input type="submit" value="Login" />
                </form>
                <a href="/learner-signup">Don't have an Account? Sign Up</a>
            </div>
        </div>
    );
}

export default LearnerSignIn;
