import React from 'react';
import { Link, Route, Switch, useHistory } from 'react-router-dom';
import './LearnerSpace.css'; // Make sure to create a CSS file for styling the page

// Import the different pages (or components) for the learner space
import YourProfile from './YourProfile';
import PurchasedCourses from './PurchasedCourses';

function LearnerSpace() {
  const history = useHistory();

  const handleLogout = () => {
    // Implement logout logic, e.g., clear user session, tokens, etc.
    // Then, redirect to the login page
    history.push('/learner-login');
  };

  return (
    <div className="learner-space">
      {/* Sidebar for navigation */}
      <nav className="sidebar">
        <ul>
          <li>
            <Link to="/learner-space/your-profile">Your Profile</Link>
          </li>
          <li>
            <Link to="/learner-space/purchased-courses">Purchased Courses</Link>
          </li>
          <li>
            <button onClick={handleLogout}>Logout</button>
          </li>
        </ul>
      </nav>

      {/* Main content area */}
      <div className="content">
        <Switch>
          <Route path="/learner-space/your-profile" component={YourProfile} />
          <Route path="/learner-space/purchased-courses" component={PurchasedCourses} />
          {/* Add more routes for other learner space pages as needed */}
        </Switch>
      </div>
    </div>
  );
}

export default LearnerSpace;
