import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css';
import logo from './Color.png';
import { Helmet } from 'react-helmet';

// Import your page components
import AboutUs from './AboutUs';
import LearnerSignIn from './LearnerSignIn';
import EducatorSignIn from './EducatorSignIn';
import ContactUs from './ContactUs';
import Home from './App'; // Assuming you have a Home component

function App() {
  return (
    <Router>
      <Helmet>
        <title>EduGate</title>
        <meta name="description" content="This is my React app" />
        <link rel="icon" href="/Color.png" type="image/x-icon" />
      </Helmet>
      <nav className="navbar">
        <div className="left">
          <img src={logo} width="200px" alt="logo" />
        </div>
        <div className="right">
          <ul className="list">
            <li><Link to="/home">Home</Link></li>
            <li><Link to="/about-us">About Us</Link></li>
            <li><Link to="/learner-signin">Learner Login</Link></li>
            <li><Link to="/educator-signin">Educator Login</Link></li>
            <li><Link to="/contact-us">Contact Us</Link></li>
          </ul>
        </div>
      </nav>
      <header>
        <h1>Welcome to the Digital Education Platform</h1>
      </header>

      <Routes>
        <Route path="/home" element={<Home />} />
        <Route path="/about-us" element={<AboutUs />} />
        <Route path="/learner-signin" element={<LearnerSignIn />} />
        <Route path="/educator-signin" element={<EducatorSignIn />} />
        <Route path="/contact-us" element={<ContactUs />} />
      </Routes>
    </Router>
  );
}

export default App;
