import React from 'react';
import { Link, Route, Routes, useNavigate } from 'react-router-dom';
import './LearnerSpace.css';
import LearnerProfile from './LearnerProfile';
import LearnerCourses from './LearnerCourses';

function LearnerSpace() {
    const navigate = useNavigate();

    const confirmLogout = () => {
        // Show a confirmation dialog to the user
        const userConfirmed = window.confirm('Are you sure you want to log out?');

        if (userConfirmed) {
            // Clear user data from session or local storage
            // You may be using session storage, local storage, or another state management method
            sessionStorage.clear(); // Example: Clear session storage
            // Alternatively, you could use localStorage.clear(); if using local storage

            // Redirect the user to the home page
            navigate('/home');
        }
    };

    return (
        <div className="learner-space">
            <nav className="sidebar">
                <ul>
                    <li><Link to="/learner-profile">Your Profile</Link></li>
                    <li><Link to="/learner-courses">Your Courses</Link></li>
                    <li><button onClick={confirmLogout}>Logout</button></li>
                </ul>
            </nav>

            <div className="content">
                <Routes>
                    <Route path="/learner-profile" element={<LearnerProfile />} />
                    <Route path="/learner-courses" element={<LearnerCourses />} />
                </Routes>
            </div>
        </div>
    );
}

export default LearnerSpace;
