import React, { useState } from 'react';

function EducatorSignIn() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Username:', username);
        console.log('Password:', password);
        setUsername('');
        setPassword('');
    };

    // Inline styles
    const containerStyle = {
        backgroundColor: '#fff',
        padding: '20px',
        borderRadius: '8px',
        boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
        width: '400px',
        height: '300px',
        position: 'absolute',
        top: '60%',
        left: '50%',
        transform: 'translate(-50%, -50%)'
    };

    const formStyle = {
        width: '100%'
    };

    const inputStyle = {
        width: '100%',
        padding: '8px',
        border: '1px solid #ccc',
        borderRadius: '5px',
        boxSizing: 'border-box'
    };

    const submitButtonStyle = {
        width: '50%',
        padding: '10px',
        backgroundColor: '#007bff',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        margin: '10px auto',
        display: 'block'
    };

    const labelStyle = {
        display: 'block',
        marginTop: '10px',
        marginBottom: '5px',
        fontSize: 'large',
        color: 'red'
    };

    const headingStyle = {
        color: '#000',
        textAlign: 'center'
    };

    const linkStyle = {
        color: '#007bff',
        textDecoration: 'none',
        marginTop: '10px',
        display: 'block',
        textAlign: 'center'
    };

    const linkHoverStyle = {
        textDecoration: 'underline'
    };

    return (
        <div style={containerStyle}>
            <h2 style={headingStyle}>Educator Login</h2>
            <form style={formStyle} onSubmit={handleSubmit}>
                <label style={labelStyle}>Username</label>
                <input
                    type="text"
                    placeholder="Username"
                    required
                    value={username}
                    onChange={handleUsernameChange}
                    style={inputStyle}
                />
                <br />
                <label style={labelStyle}>Password</label>
                <input
                    type="password"
                    placeholder="Password"
                    required
                    value={password}
                    onChange={handlePasswordChange}
                    style={inputStyle}
                />
                <br />
                <input type="submit" value="Login" style={submitButtonStyle} />
            </form>
            <a href="/educator-signup" style={linkHoverStyle} onMouseOver={() => linkStyle.textDecoration = 'underline'}>Don't have an Account? Sign Up</a>
        </div>
    );
}

export default EducatorSignIn;
