import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
function LearnerSpace({ learnerId }) {
    const navigate = useNavigate();

    const confirmLogout = () => {
        // Show a confirmation dialog to the user
        const userConfirmed = window.confirm('Are you sure you want to log out?');

        if (userConfirmed) {
            // Clear user data from session or local storage
            sessionStorage.clear(); // Clear session storage
            
            // Redirect the user to the home page
            navigate('/home');
        }
    };
    
    const linkStyle = {
        textDecoration: 'none', // Removes underline
        color: 'inherit', // Keeps the color of the text
    };

    return (
        <div className="learner-space">
            <nav className="sidebar">
                <ul>
                    {/* Include learner ID in the URLs */}
                    <li><Link to={`/learner-profile/${learnerId}`} style={linkStyle}>Your Profile</Link></li>
                    <li><Link to={`/learner-courses/${learnerId}`} style={linkStyle}>Your Courses</Link></li>
                    {/* Add more navigation links with learner ID as needed */}
                </ul>
                <button onClick={confirmLogout}>Logout</button>
            </nav>
        </div>
    );
}

export default LearnerSpace;
