// CoursePreview.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from './api';
import './CoursePreview.css'; // Import your CSS file for styling

function CoursePreview() {
  const learner_id = sessionStorage.getItem('learnerId');
  const { course_id } = useParams(); // Extract course_id from URL params
  const [courseDetails, setCourseDetails] = useState(null); // State to store course details

  useEffect(() => {
    // Fetch course details based on course_id
    async function fetchCourseDetails() {
      try {
        const response = await api.get(`/course-preview/${course_id}`);
        const { data } = response;
        setCourseDetails(data); // Set fetched course details in state
      } catch (error) {
        console.error('Error fetching course details:', error);
      }
    }

    fetchCourseDetails(); // Call the fetch function
  }, [course_id]); // Fetch data when course_id changes

  return (
    <div className="course-preview-container">
      {/* Conditional rendering of course details */}
      {courseDetails ? (
        <div className="course-preview-details">
          <h2>{courseDetails.course_name}</h2>
          <img src={courseDetails.course_thumbnail_url} alt={courseDetails.course_name} />
          <p>Description: {courseDetails.course_description}</p>
          <p>Educator: {courseDetails.educator_name}</p>
          <p>Duration: {courseDetails.course_duration}</p>
          <p>Domain: {courseDetails.course_domain}</p>
          <p>Price: {courseDetails.course_price}</p><br></br>
          <a href={`/enroll-course?course_id=${course_id}&learner_id=${learner_id}`}>Buy this course</a>
        </div>

        

      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default CoursePreview;
