// LearnerProfile.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function LearnerCourses() {
    const { learnerId } = useParams();
    const [learnerCourses, setLearnerCourses] = useState([]);

    useEffect(() => {
        fetchLearnerCourses();
    }, []);

    const fetchLearnerCourses = () => {
        fetch(`/api/learner-courses/${learnerId}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch learner courses');
                }
                return response.json();
            })
            .then(data => {
                setLearnerCourses(data.filter(course => course.course_purchase_status));
            })
            .catch(error => console.error('Error fetching learner courses:', error.message));
    };

    return (
        <div>
            <h1>Learner Profile</h1>
            <div className="course-cards">
                {learnerCourses.map(course => (
                    <div key={course.course_id} className="course-card">
                        <h3>{course.course_name}</h3>
                        <p>{course.course_description}</p>
                        {/* Add other course details as needed */}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default LearnerCourses;
