import React, { useState } from 'react';
import api from './api';

function AddCourse() {
    const [formData, setFormData] = useState({
        courseName: '',
        courseDescription: '',
        courseThumbnail: null,
        courseDuration: '',
        coursePrice: '',
        numberOfVideos: 1,
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleFileChange = (e) => {
        setFormData(prevState => ({
            ...prevState,
            courseThumbnail: e.target.files[0]
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Send formData to the backend using API call
            const response = await api.post('/add-course', formData);
            console.log('Course added successfully:', response.data);
            // Redirect or show success message
        } catch (error) {
            console.error('Error adding course:', error);
            // Handle error
        }
    };

    return (
        <div className="add-course-container">
            <h2>Add Course</h2>
            <form onSubmit={handleSubmit}>
                {/* Input fields for course details */}
                {/* Course Name */}
                {/* Course Description */}
                {/* Course Thumbnail (file input) */}
                {/* Course Duration */}
                {/* Course Price */}
                {/* Dropdown for number of videos */}
                {/* Dynamically render input fields for videos and exercises */}
                {/* Submit button */}
            </form>
        </div>
    );
}

export default AddCourse;
