import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from './api';
import './LearnerSignIn.css';

function LearnerSignIn() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [csrfToken, setCsrfToken] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchCsrfToken() {
            try {
                const response = await api.get('/get-learner-csrf-token/');
                setCsrfToken(response.data.csrfToken);
            } catch (error) {
                console.error('Error fetching CSRF token:', error);
                // Handle error if unable to fetch CSRF token
            }
        }

        fetchCsrfToken();
    }, []);

    useEffect(() => {
        // Set background image for the body
        document.body.style.backgroundImage = "url('/e-learning.jpg')";
        document.body.style.backgroundSize = 'cover';
        document.body.style.backgroundPosition = 'center';
        document.body.style.backgroundRepeat = 'no-repeat';
    
        // Clean up function
        return () => {
          // Reset background image when component unmounts
          document.body.style.backgroundImage = 'none';
        };
      }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await api.post('/learner-login/', { email, password }, { headers: {
                'X-CSRFToken': csrfToken,
            }});
            if (response.data.success) {
                const learnerId = response.data.learner_id;
                navigate(`/learner-space/${learnerId}`);
            } else {
                alert(response.data.message);
            }
        } catch (error) {
            console.error('Error during login:', error);
            alert('Failed to login. Please try again.');
        }
    };

    return (
        <div className="learner-signin">
            <header>
                <h1>Welcome to Learner's Space.</h1>
            </header>
            <div className="container3">
                <h2>Learner Login</h2>
                <form onSubmit={handleSubmit}>
                    <label>Email</label>
                    <input
                        type="text"
                        placeholder="email"
                        name="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <br />
                    <label>Password</label>
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <br />
                    <input type="submit" value="Login" />
                </form>
                <a href="/learner-signup">Don't have an Account? Sign Up</a>
            </div>
        </div>
    );
}

export default LearnerSignIn;
