import React, { useState, useEffect } from 'react';
import api from './api';
import './AdminCourses.css';

function AdminCourses() {
    const [requestedCourses, setRequestedCourses] = useState([]);
    const [releasedCourses, setReleasedCourses] = useState([]);

    useEffect(() => {
        async function fetchCourses() {
            try {
                const response = await api.get('/admin-courses');
                setRequestedCourses(response.data.filter(course => course.course_release_status === 0));
                setReleasedCourses(response.data.filter(course => course.course_release_status === 1));
            } catch (error) {
                console.error('Error fetching courses:', error);
            }
        }

        fetchCourses();
    }, []);

    const handleRelease = async (courseId) => {
        try {
            await api.post(`/release-course/${courseId}`);
            // Refetch courses after release
            fetchCourses();
        } catch (error) {
            console.error('Error releasing course:', error);
        }
    };

    const handleReject = async (courseId, reason) => {
        try {
            await api.post(`/reject-course/${courseId}`, { reason });
            // Refetch courses after rejection
            fetchCourses();
        } catch (error) {
            console.error('Error rejecting course:', error);
        }
    };

    const handleDelete = async (courseId, reason) => {
        try {
            await api.delete(`/delete-course/${courseId}`, { reason });
            // Refetch courses after deletion
            fetchCourses();
        } catch (error) {
            console.error('Error deleting course:', error);
        }
    };

    return (
        <div>
            <h2>Release Requested Courses</h2>
            <table>
                <thead>
                    <tr>
                        <th>Course ID</th>
                        <th>Course Name</th>
                        <th>Educator ID</th>
                        <th>Educator Name</th>
                        <th>Price</th>
                        <th>Actions</th>
                        <th>Reject Reason</th>
                    </tr>
                </thead>
                <tbody>
                    {requestedCourses.map(course => (
                        <tr key={course.course_id}>
                            <td>{course.course_id}</td>
                            <td>{course.course_name}</td>
                            <td>{course.educator_id}</td>
                            <td>{course.educator_name}</td>
                            <td>{course.course_price}</td>
                            <td>
                                <button onClick={() => handleRelease(course.course_id)}>Release</button>
                                <button onClick={() => handleReject(course.course_id, 'Some reason')}>Reject</button>
                            </td>
                            <td><input type="text" /></td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Released Courses</h2>
            <table>
                <thead>
                    <tr>
                        <th>Course ID</th>
                        <th>Course Name</th>
                        <th>Educator ID</th>
                        <th>Educator Name</th>
                        <th>Price</th>
                        <th>Actions</th>
                        <th>Delete Reason</th>
                    </tr>
                </thead>
                <tbody>
                    {releasedCourses.map(course => (
                        <tr key={course.course_id}>
                            <td>{course.course_id}</td>
                            <td>{course.course_name}</td>
                            <td>{course.educator_id}</td>
                            <td>{course.educator_name}</td>
                            <td>{course.course_price}</td>
                            <td><button onClick={() => handleDelete(course.course_id, 'Some reason')}>Delete</button></td>
                            <td><input type="text" /></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default AdminCourses;
