import React, { useEffect, useState } from 'react';
import './LearnerCourses.css'; // Import any styles for this component

function LearnerCourses() {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    // Fetch purchased courses data from your backend service
    // This is an example; replace the URL with your actual endpoint
    fetch('/api/learner-courses')
      .then(response => response.json())
      .then(data => setCourses(data.courses))
      .catch(error => console.error('Error fetching courses:', error));
  }, []);

  return (
    <div className="learner-courses">
      <h2>Purchased Courses</h2>
      <ul>
        {courses.map(course => (
          <li key={course.id}>
            <div className="course-info">
              <h3>{course.title}</h3>
              <p>{course.description}</p>
              {/* Add more course information here */}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default LearnerCourses;
