import React from 'react';
import { Link, Route, Routes, useNavigate } from 'react-router-dom';
import './LearnerSpace.css'; // Make sure to create a CSS file for styling the page

// Import the different pages (or components) for the learner space
import LearnerProfile from './LearnerProfile';
import LearnerCourses from './LearnerCourses';

function LearnerSpace() {
    const navigate = useNavigate();
  
    const handleLogout = () => {
      // Implement logout logic and redirect
      navigate('/learner-login');
    };
  
    return (
      <div className="learner-space">
        <nav className="sidebar">
          <ul>
            <li><Link to="/learner-space/learner-profile">Your Profile</Link></li>
            <li><Link to="/learner-space/learner-courses">Your Courses</Link></li>
            <li><button onClick={handleLogout}>Logout</button></li>
          </ul>
        </nav>
  
        <div className="content">
          <Routes>
            <Route path="/learner-profile" element={<LearnerProfile />} />
            <Route path="/learner-courses" element={<LearnerCourses />} />
          </Routes>
        </div>
      </div>
    );
  }
  
  export default LearnerSpace;
