import React, { useState, useEffect } from 'react';
import api from './api';
import './Courses.css';

function Courses() {
  const [coursesByDomain, setCoursesByDomain] = useState({});

  useEffect(() => {
    fetchReleasedCourses();
  }, []);

  async function fetchReleasedCourses() {
    try {
      const response = await api.get('/get-released-courses');
      const { data } = response;

      if (typeof data === 'object') {
        const courses = Object.values(data);

        // Group courses by domain
        const categorizedCourses = {};
        courses.forEach(course => {
          if (!categorizedCourses[course.course_domain]) {
            categorizedCourses[course.course_domain] = [course];
          } else {
            categorizedCourses[course.course_domain].push(course);
          }
        });

        setCoursesByDomain(categorizedCourses);
      } else {
        console.error('Invalid response format:', data);
      }
    } catch (error) {
      console.error('Error fetching released courses:', error);
    }
  }

  // Function to handle preview button click
  const handlePreviewClick = (courseId) => {
    // Call the API endpoint for course preview with courseId
    // Example: api.get(`/course-preview/${courseId}`);
    console.log(`Preview clicked for course ID: ${courseId}`);
  };

  return (
    <div className="courses">
      <header>
        <h1>Explore and pick courses of your Interests</h1>
      </header>
      <div className="course-categories">
        {/* Iterate over each domain */}
        {Object.entries(coursesByDomain).map(([domain, courses]) => (
          <div key={domain}>
            <h2>{domain}</h2>
            <div className="course-cards">
              {/* Iterate over courses within the domain */}
              {courses.map(course => (
                <div className="course-card" key={course.course_id}>
                  <img src={course.course_thumbnail_url} alt={course.course_name} />
                  <div className="course-details">
                    <h3>{course.course_name}</h3>
                    <p>Educator: {course.educator_name}</p>
                    <p>Duration: {course.course_duration}</p>
                    <p>Price: {course.course_price}</p>
                    {/* Preview button */}
                    <button onClick={() => handlePreviewClick(course.course_id)}>Preview</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Courses;
