import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from './api';
import './AddCourse.css';

function AddCourse({ educatorId }) {
    const [courseName, setCourseName] = useState('');
    const [courseDescription, setCourseDescription] = useState('');
    const [courseThumbnail, setCourseThumbnail] = useState('');
    const [courseDuration, setCourseDuration] = useState('');
    const [coursePrice, setCoursePrice] = useState('');
    const [numVideos, setNumVideos] = useState(1);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Make API request to add course
            await api.post('/add-course', {
                educatorId,
                courseName,
                courseDescription,
                courseThumbnail,
                courseDuration,
                coursePrice,
                numVideos
            });
            // Navigate to educator courses page
            navigate(`/educator-courses/${educatorId}`);
        } catch (error) {
            console.error('Error adding course:', error);
            // Handle error
        }
    };

    const handleNumVideosChange = (e) => {
        setNumVideos(parseInt(e.target.value));
    };

    return (
        <div className="add-course">
            <h2>Add Course</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="courseName">Course Name:</label>
                <input type="text" id="courseName" value={courseName} onChange={(e) => setCourseName(e.target.value)} required />
                <label htmlFor="courseDescription">Course Description:</label>
                <textarea id="courseDescription" value={courseDescription} onChange={(e) => setCourseDescription(e.target.value)} required />
                <label htmlFor="courseThumbnail">Course Thumbnail URL:</label>
                <input type="text" id="courseThumbnail" value={courseThumbnail} onChange={(e) => setCourseThumbnail(e.target.value)} required />
                <label htmlFor="courseDuration">Course Duration:</label>
                <input type="text" id="courseDuration" value={courseDuration} onChange={(e) => setCourseDuration(e.target.value)} required />
                <label htmlFor="coursePrice">Course Price:</label>
                <input type="text" id="coursePrice" value={coursePrice} onChange={(e) => setCoursePrice(e.target.value)} required />
                <label htmlFor="numVideos">Number of Videos:</label>
                <select id="numVideos" value={numVideos} onChange={handleNumVideosChange}>
                    {[...Array(100).keys()].map(num => (
                        <option key={num} value={num + 1}>{num + 1}</option>
                    ))}
                </select>
                {/* Render additional input fields for videos based on numVideos state */}
                {[...Array(numVideos).keys()].map(index => (
                    <div key={index}>
                        <label htmlFor={`video${index + 1}`}>{`Video ${index + 1}:`}</label>
                        <input type="text" id={`video${index + 1}`} required />
                        <label htmlFor={`exercise${index + 1}`}>{`Exercise ${index + 1}:`}</label>
                        <input type="text" id={`exercise${index + 1}`} required />
                    </div>
                ))}
                <input type="submit" value="Submit" />
            </form>
        </div>
    );
}

export default AddCourse;
