import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import api from './api'; // Import your API utility
import './LearnerSignIn.css';

function LearnerSignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send the email and password to the backend
      const response = await api.post('/learner-login/', {
        email,
        password,
      });

      // Check if the login was successful
      if (response.data.success) {
        // Redirect to learner space page
        history.push('/learner-space');
      } else {
        alert('Invalid email or password.');
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert('Failed to login. Please try again.');
    }
  };

  return (
    <div className="learner-signin">
      <header>
        <h1>Welcome to Learner's Space.</h1>
      </header>
      <div className="container3">
        <h2>Learner Login</h2>
        <form onSubmit={handleSubmit}>
          <label>Email</label>
          <input
            type="text"
            placeholder="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <br />
          <label>Password</label>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <br />
          <input type="submit" value="Login" />
        </form>
        <a href="/learner-signup">Don't have an Account? Sign Up</a>
      </div>
    </div>
  );
}

export default LearnerSignIn;
