import React, { useState } from 'react';
import './LearnerSignUp.css';
import api, { checkLearnerUniqueFields } from './api';
import { debounce } from 'lodash';

let globalRequestId = 0;

function LearnerSignUp() {
    const [formData, setFormData] = useState({
        name: '',
        dob: '',
        highest_qualification: '',
        email: '',
        mobile_no: '',
        username: '',
        password: '',
    });

    const [otp, setOtp] = useState('');
    const [otpSent, setOtpSent] = useState(false);
    const [isOtpVerified, setIsOtpVerified] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [requestId, setRequestId] = useState(globalRequestId);
    const [errorFields, setErrorFields] = useState({
        email: '',
        mobile_no: '',
        username: '',
    });

    const handleFieldBlur = (e) => {
        const { name } = e.target;
        if (name === 'email' || name === 'mobile_no' || name === 'username') {
            debouncedCheckLearnerUniqueFields(formData, setErrorFields);
        }
    };

    const debouncedCheckLearnerUniqueFields = debounce(async () => {
        const currentRequestId = ++globalRequestId;
        setRequestId(currentRequestId);

        try {
            const response = await api.post('/check-learner-unique-fields/', {
                email: formData.email,
                mobile_no: formData.mobile_no,
                username: formData.username,
            });

            if (currentRequestId === requestId) {
                const data = response.data;
                setErrorFields({
                    email: data.email ? '' : 'Email is already in use.',
                    mobile_no: data.mobile_no ? '' : 'Mobile number is already in use.',
                    username: data.username ? '' : 'Username is already in use.',
                });
            }
        } catch (error) {
            console.error('Error checking unique fields:', error);
        }
    }, 300);

    const [passwordErrors, setPasswordErrors] = useState([]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });

        if (name === 'password') {
            const errors = validatePassword(value);
            setPasswordErrors(errors);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Check password errors first
        if (passwordErrors.length > 0) {
            alert('Please fix password errors before submitting.');
            return;
        }

        // Check for unique fields
        try {
            const response = await checkLearnerUniqueFields(
                formData.email,
                formData.mobile_no,
                formData.username
            );

            // Set error messages based on the response
            setErrorFields({
                email: response.email ? '' : 'Email is already in use.',
                mobile_no: response.mobile_no ? '' : 'Mobile number is already in use.',
                username: response.username ? '' : 'Username is already in use.',
            });

            // Check if there are any errors
            const hasErrors = !response.email || !response.mobile_no || !response.username;
            if (hasErrors) {
                alert('Please resolve the highlighted errors before submitting the form.');
                return;
            }
        } catch (error) {
            console.error('Error checking unique fields:', error);
            alert('Failed to check unique fields. Please try again.');
            return;
        }

        // Proceed to send OTP if all fields are unique
        setIsLoading(true);
        try {
            const response = await api.post('/learner-send-otp/', { email: formData.email });
            alert(response.data.message);
            setOtpSent(true);
        } catch (error) {
            console.error('Error sending OTP:', error);
            alert('Failed to send OTP. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleOTPVerification = async (e) => {
        e.preventDefault();
        setIsLoading(true);
    
        try {
            const response = await api.post('/learner-verify-otp/', {
                email: formData.email,
                otp: otp,
            });

            if (response.data.success) {
                setIsOtpVerified(true);
                alert('OTP verified successfully!');
                // Proceed to learner signup
                await handleLearnerSignup();
            } else {
                alert('Incorrect OTP. Please try again.');
            }
        } catch (error) {
            console.error('Error verifying OTP:', error);
            alert('Error verifying OTP. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleResendOTP = async () => {
        setIsLoading(true);
        try {
            const response = await api.post('/learner-send-otp/', {
                email: formData.email,
            });
            alert(response.data.message);
            setOtpSent(true);
            setOtp(''); // Clear OTP input
        } catch (error) {
            console.error('Error sending OTP:', error);
            alert('Failed to send OTP. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    // Handle learner sign-up
    const handleLearnerSignup = async () => {
        try {
            const dataToSend = {
                ...formData,
                otp: otp,
            };

            // Make a POST request to the learner signup endpoint
            const response = await api.post('/learner-signup/', dataToSend);

            if (response.status === 201) {
                alert('Learner signed up successfully!');
                // Perform any other navigation or state updates as needed
            } else {
                alert(`Error: ${response.data.message}`);
            }
        } catch (error) {
            console.error('Error during signup:', error);
            alert('Error during signup. Please try again.');
        }
    };

    return (
        <div className="container4">
            <h2>Learner Sign Up</h2>
            {isLoading && <p>Loading...</p>}
            {!isOtpVerified ? (
                <>
                    {otpSent ? (
                        <form onSubmit={handleOTPVerification}>
                            <label>Enter OTP</label>
                            <input
                                type="text"
                                value={otp}
                                onChange={(e) => setOtp(e.target.value)}
                                required
                            />
                            <input type="submit" value="Verify OTP" />
                            <button type="button" onClick={handleResendOTP}>
                                Resend OTP
                            </button>
                        </form>
                    ) : (
                        <form onSubmit={handleSubmit}>
                            <label>Full Name</label>
                            <input
                                type="text"
                                name="name"
                                placeholder="e.g. Thomas Alva Edison"
                                value={formData.name}
                                onChange={handleInputChange}
                                required
                            />
                            <br />

                            <label>Date of Birth</label>
                            <input
                                type="date"
                                name="dob"
                                value={formData.dob}
                                onChange={handleInputChange}
                                required
                            />
                            <br />

                            <label>Highest Qualification</label>
                            <select
                                name="highest_qualification"
                                value={formData.highest_qualification}
                                onChange={handleInputChange}
                                required
                            >
                                <option value="" disabled>
                                    Select your qualification
                                </option>
                                <option value="Grade 1">Grade 1</option>
                                <option value="Grade 2">Grade 2</option>
                                <option value="Grade 3">Grade 3</option>
                                <option value="Grade 4">Grade 4</option>
                                <option value="Grade 5">Grade 5</option>
                                <option value="Grade 6">Grade 6</option>
                                <option value="Grade 7">Grade 7</option>
                                <option value="Grade 8">Grade 8</option>
                                <option value="Grade 9">Grade 9</option>
                                <option value="Grade 10">Grade 10</option>
                                <option value="Grade 11">Grade 11</option>
                                <option value="Grade 12">Grade 12</option>
                                <option value="B.A.">Bachelor of Arts (B.A.)</option>
                                <option value="B.Sc.">Bachelor of Science (B.Sc.)</option>
                                <option value="B.Com">Bachelor of Commerce (B.Com)</option>
                                <option value="B.E.">Bachelor of Engineering (B.E.)</option>
                                <option value="B.Tech">Bachelor of Technology (B.Tech)</option>
                                <option value="B.Pharm">Bachelor of Pharmacy (B.Pharm)</option>
                                <option value="B.Arch">Bachelor of Architecture (B.Arch)</option>
                                <option value="BBA">Bachelor of Business Administration (BBA)</option>
                                <option value="BCA">Bachelor of Computer Applications (BCA)</option>
                                <option value="MBBS">Bachelor of Medicine, Bachelor of Surgery (MBBS)</option>
                                <option value="BDS">Bachelor of Dental Surgery (BDS)</option>
                                <option value="B.V.Sc.">Bachelor of Veterinary Science (B.V.Sc.)</option>
                                <option value="LL.B">Bachelor of Laws (LL.B)</option>
                                <option value="M.A.">Master of Arts (M.A.)</option>
                                <option value="M.Sc.">Master of Science (M.Sc.)</option>
                                <option value="M.Com">Master of Commerce (M.Com)</option>
                                <option value="M.E.">Master of Engineering (M.E.)</option>
                                <option value="M.Tech">Master of Technology (M.Tech)</option>
                                <option value="MBA">Master of Business Administration (MBA)</option>
                                <option value="MCA">Master of Computer Applications (MCA)</option>
                                <option value="MD">Doctor of Medicine (MD)</option>
                                <option value="MS">Master of Surgery (MS)</option>
                                <option value="M.Pharm">Master of Pharmacy (M.Pharm)</option>
                                <option value="LL.M">Master of Laws (LL.M)</option>
                                <option value="Ph.D">Doctor of Philosophy (Ph.D)</option>
                            </select>
                            <br></br>
                            <label>Email</label>
                            <input
                                type="text"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                onBlur={handleFieldBlur}
                                required
                            />
                            {errorFields.email && (
                                <p style={{ color: 'red' }}>
                                    {errorFields.email}
                                </p>
                            )}
                            <br />

                            <label>Mobile No</label>
                            <input
                                type="text"
                                name="mobile_no"
                                value={formData.mobile_no}
                                onChange={handleInputChange}
                                onBlur={handleFieldBlur}
                                required
                            />
                            {errorFields.mobile_no && (
                                <p style={{ color: 'red' }}>
                                    {errorFields.mobile_no}
                                </p>
                            )}
                            <br />

                            <label>Username</label>
                            <input
                                type="text"
                                name="username"
                                value={formData.username}
                                onChange={handleInputChange}
                                onBlur={handleFieldBlur}
                                required
                            />
                            {errorFields.username && (
                                <p style={{ color: 'red' }}>
                                    {errorFields.username}
                                </p>
                            )}
                            <br />

                            <label>Password</label>
                            <input
                                type="password"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                required
                            />
                            {passwordErrors.map((error, index) => (
                                <p key={index} style={{ color: 'red' }}>
                                    {error}
                                </p>
                            ))}
                            <br />

                            <input type="submit" value="Sign Up" />
                        </form>
                    )}
                    <a href="/learner-signin">Already have an Account? Sign In</a>
                </>
            ) : (
                <div>
                    <p>OTP verified successfully! Proceed with the next steps.</p>
                </div>
            )}
        </div>
    );
}

export default LearnerSignUp;
