import './LearnerProfile.css'; // Import any styles for this component
import React, { useEffect, useState } from 'react';
import api from './api'; // Import your API instance

function LearnerProfile() {
    const [learnerData, setLearnerData] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Fetch the learner's profile data from the API
        async function fetchProfileData() {
            try {
                const response = await api.get('/learner-profile/');
                setLearnerData(response.data);
            } catch (err) {
                setError('Failed to fetch profile data. Please try again.');
            } finally {
                setLoading(false);
            }
        }

        fetchProfileData();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2>My Profile</h2>
            <p><strong>Name:</strong> {learnerData.name}</p>
            <p><strong>Date of Birth:</strong> {learnerData.dob}</p>
            <p><strong>Email:</strong> {learnerData.email_id}</p>
            <p><strong>Mobile Number:</strong> {learnerData.mobile_no}</p>
            <p><strong>Highest Qualification:</strong> {learnerData.highest_qualification}</p>
            <p><strong>Username:</strong> {learnerData.username}</p>
        </div>
    );
}

export default LearnerProfile;
